import React, {useEffect, useContext, useState} from 'react';
import { AccountContext } from './account';
import { CognitoUserAttribute } from './account';

export default () => {

    const [plan, setPlan] = useState('');
    const { useEffect, useContext, useState } = require("react")
    const {getSession} = useContext(AccountContext);

    useEffect (() => {
        getSession().then(data => {
            console.log("DATA: ", data);
        });
    }, []);

    const onSubmit = event => {
        event.PreventDefautl();

        getSession.then(({user}) => {
            const attributes = [
                new CognitoUserAttribute({Name:'custom:plan', value: plan})
            ];

            user.UpdateAttributes(attributes, (err, result) => {
                if(err) console.error(err);
                console.log(result);
            });
        });


    }

    return (
        <div>
            <h1>Actualizar tu plan: </h1>
            <form onSubmit={onSubmit}>
                <input value={plan} onChange={event => setPlan(event.target.value)}></input>
                <button type="submit">Actualizar</button>
            </form>
        </div>
    )
}
